&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(channel.isClosed()){
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(&#34;exit-status:&nbsp;&#34;+channel.getExitStatus());
